pragma solidity ^0.5.4;

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");

        return c;
    }
    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return sub(a, b, "SafeMath: subtraction overflow");
    }
    function sub(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;

        return c;
    }
    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }

        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");

        return c;
    }
    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return div(a, b, "SafeMath: division by zero");
    }
    function div(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b > 0, errorMessage);
        uint256 c = a / b;
        // assert(a == b * c + a % b); // There is no case in which this doesn't hold

        return c;
    }
}

contract LuckyGunWithZeroPointOne {
    using SafeMath for uint256;
    
    address public authorizedAddress;
    address public recoveryAddress;

    bool public initialized;

    bool public isInitGame;
    bool public isSetGameResut;
    bool public isSettleUpGame;
    // uint256 public startTime;
    // uint256 public expiredTime;
    uint256 public betAmount = 0.1 ether;
    uint256 public playerTotalNumber = 6;
    // uint256 public winnerNumer;
    address payable public platformAddress;

    uint256 public playerCurrentNumber;
    mapping (uint256 => address payable) public noncesToPlayer;  // nonces -> player address, start from 0

    modifier onlyOnce() {
        require(!initialized, "must not already be initialized");
        initialized = true;
        _;
    }
    
    modifier onlyRecoveryAddress() {
        require(msg.sender == recoveryAddress, "sender must be recovery address");
        _;
    }
    
    modifier onlyAuthorizedAddress() {
        require(msg.sender == authorizedAddress, "sender must be authorized address");
        _;
    }
    
    event Authorized(address authorizedAddress);
    event EmergencyRecovery(address authorizedAddress);
    event Bet(address indexed playerAddress);
    event GameSettleUp(address winner, address loser);


    event Log(bytes);

    function init(address _authorizedAddress, address _recoveryAddress, address payable _platformAddress) public onlyOnce {
        authorizedAddress = _authorizedAddress;
        recoveryAddress = _recoveryAddress;
        platformAddress = _platformAddress;
        
        isInitGame = true;
        
        emit Authorized(_authorizedAddress);
    }
    
    function() external payable {
        revert();
    }
    
    function emergencyRecovery(address _authorizedAddress) external onlyRecoveryAddress {
        require(_authorizedAddress != address(0), "Authorized addresses must not be zero.");
        require(_authorizedAddress != recoveryAddress, "Do not use the recovery address as an authorized address.");

        authorizedAddress = _authorizedAddress;

        emit EmergencyRecovery(_authorizedAddress);
    }

    function playerBet() payable public {
        require(isInitGame, "game has not initialized");
        // require(now<=expiredTime, "bet disable");
        // require(!isSetGameResut, "game result has initialized");
        require(!isSettleUpGame, "game has settled up");
        require(playerCurrentNumber<playerTotalNumber, "player overflow");
        require(msg.value == betAmount);
        
        noncesToPlayer[playerCurrentNumber] = msg.sender;
     
        playerCurrentNumber = playerCurrentNumber + 1;

        emit Bet(msg.sender);
    }
    
    function settleUp() public onlyAuthorizedAddress {
        require(playerCurrentNumber == playerTotalNumber, "game not end");
        require(!isSettleUpGame, "game has settled up");

        isSettleUpGame = true;
        
        uint winner = uint(keccak256(abi.encodePacked(block.timestamp+block.number+uint(blockhash(block.number)))))%6;
        uint loser = uint(keccak256(abi.encodePacked(block.timestamp+block.number+uint(blockhash(block.number))+winner)))%6;
        if(winner == loser){
            loser = (winner+1)%6;
        }
        
        for (uint i=0; i<playerTotalNumber; i++) {
            if (i == winner) {
                noncesToPlayer[i].transfer(0.12 ether);
            } else if (i == loser) {
                
            } else {
                noncesToPlayer[i].transfer(0.11 ether);
            }
        }
        
        platformAddress.transfer(0.04 ether);
        
        emit GameSettleUp(noncesToPlayer[winner], noncesToPlayer[loser]);   

    }
}
